package com.google.codeu.models;

import java.net.URL;
import java.util.UUID;

/**
 * Class representing an image in a post.
 */
public class PostImage {

    private UUID id;
    private URL imageUrl;
    private String imageDescription;

    /**
     * Construct a new <code>PostImage</code> object with the image's URL and its
     * description. Its ID will be randomly generated by the server.
     * 
     * @param imageURL         The image's URL.
     * @param imageDescription The image's description.
     */
    public PostImage(URL imageURL, String imageDescription) {
        this(UUID.randomUUID(), imageURL, imageDescription);
    }

    /**
     * Construct a new <code>PostImage</code> in full details.
     * 
     * @param id               The image's id.
     * @param imageURL         The image's URL.
     * @param imageDescription The image's description.
     */
    public PostImage(UUID id, URL imageURL, String imageDescription) {
        this.id = id;
        this.imageUrl = imageURL;
        this.imageDescription = imageDescription;
    }

    public UUID getId() {
        return id;
    }

    public URL getImageUrl() {
        return imageUrl;
    }

    public String getImageDescription() {
        return imageDescription;
    }
}